const form = document.getElementById("formSuscripcion");
const mensaje = document.getElementById("mensaje");

if (form) {
    form.addEventListener("submit", function (e) {
        e.preventDefault();

        const nombre = form.elements.nombre.value.trim();
        const email = form.elements.email.value.trim();
        const suscripcion = form.elements.suscripcion.value;
        const seccion = form.elements.seccion.value;
        const condiciones = document.getElementById("condiciones").checked;

        if (nombre === "") {
            mostrarError("El nombre no puede estar vacío.");
            return;
        }

        if (email === "" || !email.includes("@")) {
            mostrarError("El email es obligatorio y debe contener @.");
            return;
        }

        if (suscripcion === "") {
            mostrarError("Debes seleccionar un tipo de suscripción.");
            return;
        }

        if (seccion === "") {
            mostrarError("Debes seleccionar una sección favorita.");
            return;
        }

        if (!condiciones) {
            mostrarError("Debes aceptar las condiciones.");
            return;
        }

        mostrarCorrecto("Formulario enviado correctamente.");
    });
}

function mostrarError(texto) {
    mensaje.style.color = "red";
    mensaje.textContent = texto;
}

function mostrarCorrecto(texto) {
    mensaje.style.color = "green";
    mensaje.textContent = texto;
}"}

