document.getElementById("formAltes").addEventListener("submit", function (event) {
  event.preventDefault();

  const nom = document.getElementById("campNom").value.trim();
  const correu = document.getElementById("campEmail").value.trim();
  const modalitat = document.getElementById("campModalitat").value;
  const area = document.getElementById("campArea").value;
  const acceptacio = document.getElementById("campAcceptacio").checked;
  const avis = document.getElementById("avís");

  let llistaErrors = [];

  // Validacions bàsiques
  if (nom === "") llistaErrors.push("Cal introduir un nom complet.");
  if (correu === "") {
    llistaErrors.push("L'adreça electrònica és obligatòria.");
  } else if (!correu.includes("@")) {
    llistaErrors.push("El correu ha de contenir el símbol '@'.");
  }

  if (modalitat === "") llistaErrors.push("Selecciona una modalitat de subscripció.");
  if (area === "") llistaErrors.push("Selecciona una categoria d'interès.");
  if (!acceptacio) llistaErrors.push("Has d'acceptar les condicions d'ús.");

  // Mostrar errors
  if (llistaErrors.length > 0) {
    avis.innerHTML = llistaErrors.join("<br>");
    avis.style.color = "crimson";
    avis.setAttribute("aria-live", "assertive");
    return;
  }

  // Formulari correcte
  avis.textContent = "La sol·licitud s'ha enviat amb èxit!";
  avis.style.color = "green";
  avis.setAttribute("aria-live", "assertive");
});"}

