const form = document.getElementById("myform");

form.addEventListener("submit", function (evento) {

    // Prevenir envío real
    evento.preventDefault();

    // Referencias
    const name = document.getElementById("name");
    const email = document.getElementById("email");
    const subs = document.getElementById("subscription");
    const condicion = document.getElementById("conditions");
    const errorBox = document.getElementById("ErrorMsg");
    const successBox = document.getElementById("SuccessMsg");
    const markedSections = document.querySelectorAll('input[name="section"]:checked'); //Busca y dame una lista de todos los elementos que cumplan estas 3 condiciones

    // Resetear estado visual
    errorBox.style.display = 'none';
    successBox.style.display = 'none';
    name.classList.remove('red-border');
    email.classList.remove('red-border');
    subs.classList.remove('red-border');
    document.getElementById('divSection').style.border = "none";
    document.getElementById('divSection').style.padding = "0";

    let errorList = [];

    // --- COMPROBACIONES ---

    // Nombre no vacío
    if (!name.checkValidity()) {
        errorList.push("El campo Nombre es obligatorio.");
        name.classList.add('red-border');
    }

    // Email no vacío y contiene @ (checkValidity valida ambas cosas gracias a 'required' y 'type=email')
    if (!email.checkValidity()) {
        errorList.push("El Email es obligatorio y debe ser válido (contener @).");
        email.classList.add('red-border');
    }

    // Tipo de suscripción seleccionado
    if (!subs.checkValidity()) {
        errorList.push("Debes seleccionar un Tipo de suscripción.");
        subs.classList.add('red-border');
    }

    // Sección seleccionada
    if (markedSections.length === 0) { // si la lista markedSections es 0
        errorList.push("Selecciona al menos una Sección favorita.");
        document.getElementById('divSection').style.border = "2px solid red";
        document.getElementById('divSection').style.padding = "10px";
    }

    // Checkbox marcado (Condiciones)
    if (!condicion.checkValidity()) {
        errorList.push("Debes aceptar las Condiciones.");
    }

    // --- RESULTADO ---
    if (errorList.length > 0) { // Si algun required esta vacio -> mensaje en rojo
        let mensajeFinal = "Por favor, revisa los errores:<br><ul>";

        // Uso un bucle para recorrer cada error de la lista
        for (let error of errorList) {      // "Para cada error que haya en errorList"
            mensajeFinal = mensajeFinal + "<li>" + error + "</li>";     // le añadimos una etiqueta <li> al mensaje final
        }
        // Cierro la lista al final del todo
        mensajeFinal = mensajeFinal + "</ul>";

        // Sale la lista que esta acumulada en errorList
        errorBox.innerHTML = mensajeFinal;
        errorBox.style.display = 'block';
    }
    else {      // Sino (si todo esta correcto) -> mensaje en verde
        successBox.style.display = 'block';
        form.reset();
    }
});"}

