document.addEventListener("DOMContentLoaded", () => {
    const form = document.getElementById("formSuscripcion");
    const msg = document.getElementById("mensajeResultado");

    form.addEventListener("submit", (e) => {
        e.preventDefault(); 

        const nombre = document.getElementById("nombre").value.trim();
        const email = document.getElementById("email").value.trim();
        const plan = document.getElementById("suscripcion").value;
        const seccion = document.getElementById("seccion").value;
        const acepta = document.getElementById("condiciones").checked;

        let errores = [];

        if (!nombre) errores.push("Falta el nombre.");
        if (!email.includes("@")) errores.push("Email incorrecto (falta @).");
        if (!plan) errores.push("Elige un plan de suscripción.");
        if (!seccion) errores.push("Elige tu sección favorita.");
        if (!acepta) errores.push("Debes aceptar las condiciones.");

        if (errores.length > 0) {
            msg.className = "mensaje-error";
            msg.innerHTML = errores.join("<br>"); 
        } else {
            msg.className = "mensaje-exito";
            msg.innerHTML = `¡Gracias ${nombre}! Suscripción correcta.`;
            form.reset(); 
        }
    });
