    <script>

        const form = document.getElementById("formSuscripcion");

        form.addEventListener("submit", function (event) {

            event.preventDefault();  // Evito que se envíe de verdad

            let mensaje = document.getElementById("mensaje");
            mensaje.style.color = "red";
            mensaje.innerHTML = "";

            let nombre = document.getElementById("nombre").value.trim();
            let email = document.getElementById("email").value.trim();
            let tipo = document.getElementById("tipo").value;
            let seccion = document.getElementById("seccion").value;
            let condiciones = document.getElementById("condiciones").checked;

            // Comprobaciones básicas
            if (nombre === "") {
                mensaje.innerHTML = "El nombre no puede estar vacío.";
                return;
            }

            if (email === "") {
                mensaje.innerHTML = "El email no puede estar vacío.";
                return;
            }

            if (email.indexOf("@") === -1) {
                mensaje.innerHTML = "El email debe llevar '@'.";
                return;
            }

            if (tipo === "") {
                mensaje.innerHTML = "Falta seleccionar un tipo de suscripción.";
                return;
            }

            if (seccion === "") {
                mensaje.innerHTML = "Falta elegir una sección favorita.";
                return;
            }

            if (!condiciones) {
                mensaje.innerHTML = "Debes aceptar las condiciones.";
                return;
            }

            // Si todo va bien
            mensaje.style.color = "green";
            mensaje.innerHTML = "Formulario enviado correctamente.";

        });

