document.addEventListener("DOMContentLoaded", () => {
    const formulario = document.getElementById("formulario");
    const mensaje = document.getElementById("mensaje");

    formulario.addEventListener("submit", function (event) {
        event.preventDefault();

        const nombre = document.getElementById("nombre").value.trim();
        const email = document.getElementById("email").value.trim();
        const sexo = document.getElementById("sexo").value;
        const suscripcion = document.getElementById("suscripcion").value;
        const seccion = document.getElementById("seccion").value;
        const condiciones = document.getElementById("condiciones").checked;

        const emailValido = email.includes("@") && email.indexOf("@") > 0 && email.indexOf("@") < email.length - 1;

        if (nombre === "" || email === "" || sexo === "" || suscripcion === "" || seccion === "" || !condiciones) {
            mensaje.innerText = "Faltan campos por completar";
            mensaje.style.color = "red";
            return;
        }

        if (!emailValido) {
            mensaje.innerText = "Correo inválido (debe contener @)";
            mensaje.style.color = "red";
            return;
        }

        mensaje.innerText = "Formulario enviado correctamente";
        mensaje.style.color = "green";
        formulario.reset();
    });
});"}

