// Obtener el formulario y el área de mensajes
const form = document.getElementById('suscripcion-form');
const msgBox = document.getElementById('validation-message');

// Función de validación principal
function validarFormulario(event) {
  // 1. Prevenir el envío real del formulario (comportamiento por defecto)
  event.preventDefault();

  // Limpiar el mensaje anterior y reiniciar clases
  msgBox.style.display = 'none';
  msgBox.classList.remove('error-msg', 'success-msg');

  let errores = [];

  // Obtener valores de los campos
  const nombre = document.getElementById('nombre').value.trim();
  const email = document.getElementById('email').value.trim();
  const suscripcion = document.getElementById('suscripcion').value;
  const condiciones = document.getElementById('condiciones').checked;

  // Obtener todas las secciones seleccionadas (checkboxes)
  // document.querySelectorAll devuelve una NodeList, por eso usamos .length
  const secciones = document.querySelectorAll('input[name="seccion"]:checked');


  // 2. Comprobar Nombre no vacío
  if (nombre === '') {
    errores.push('El campo Nombre completo es obligatorio.');
  }

  // 3. Comprobar Email no vacío y contiene @
  if (email === '') {
    errores.push('El campo Correo electrónico es obligatorio.');
  } else if (!email.includes('@')) {
    errores.push('El Correo electrónico debe contener un @ válido.');
  }

  // 4. Comprobar Tipo de suscripción seleccionado (no es el valor vacío)
  // El valor vacío en HTML es "" gracias a la opción 'disabled selected'
  if (suscripcion === '') {
    errores.push('Debes seleccionar un Tipo de suscripción.');
  }

  // 5. Comprobar Sección favorita seleccionada (mínimo 1 checkbox)
  if (secciones.length === 0) {
    errores.push('Debes seleccionar al menos una Sección favorita.');
  }

  // 6. Comprobar Checkbox de condiciones marcado
  if (!condiciones) {
    errores.push('Debes aceptar las condiciones de uso.');
  }

  // 7. Mostrar resultados
  if (errores.length > 0) {
    // Si falla = mensaje en rojo
    msgBox.classList.add('error-msg');
    msgBox.innerHTML = '<strong>Error en la suscripción:</strong><ul>' + 
                       errores.map(e => `<li>${e}</li>`).join('') + 
                       '</ul>';
    msgBox.style.display = 'block';
  } else {
    // Si todo correcto = mensaje en verde

    // Mostrar la información recogida en la consola para confirmar
    console.log("--- SUSCRIPCIÓN EXITOSA ---");
    console.log("Nombre:", nombre);
    console.log("Email:", email);
    console.log("Suscripción:", suscripcion);
    console.log("Secciones seleccionadas:", Array.from(secciones).map(s => s.value));

    msgBox.classList.add('success-msg');
    msgBox.innerHTML = '<strong>¡Suscripción correcta!</strong> Gracias, ' + nombre + '. Pronto recibirás un correo de confirmación.';
    msgBox.style.display = 'block';

    // Opcional: limpiar el formulario tras el éxito
    // form.reset(); 
  }
}

// Asignar la función al evento submit del formulario
form.addEventListener('submit', validarFormulario);"}

