// Validación del formulario
document.getElementById("formSuscripcion").addEventListener("submit", function(event) {
  event.preventDefault(); // Evita el envío real

  // Capturamos valores
  let nombre = document.getElementById("nombre").value.trim();
  let email = document.getElementById("email").value.trim();
  let suscripcion = document.getElementById("suscripcion").value;
  let seccion = document.querySelector("input[name='seccion']:checked");
  let condiciones = document.getElementById("condiciones").checked;

  let mensaje = document.getElementById("mensaje");

  // Validaciones
  if (nombre === "") {
    mensaje.textContent = "Error: El nombre no puede estar vacío.";
    mensaje.style.color = "red";
    return;
  }

  if (email === "" || !email.includes("@")) {
    mensaje.textContent = "Error: El correo debe contener @.";
    mensaje.style.color = "red";
    return;
  }

  if (suscripcion === "") {
    mensaje.textContent = "Error: Debes seleccionar un tipo de suscripción.";
    mensaje.style.color = "red";
    return;
  }

  if (!seccion) {
    mensaje.textContent = "Error: Debes elegir una sección favorita.";
    mensaje.style.color = "red";
    return;
  }

  if (!condiciones) {
    mensaje.textContent = "Error: Debes aceptar las condiciones.";
    mensaje.style.color = "red";
    return;
  }

  // Si todo está correcto
  mensaje.textContent = "¡Suscripción enviada correctamente!";
  mensaje.style.color = "green";
});"}

