/****************************************************
 * 1) FORMULARIO CON JAVASCRIPT
 ****************************************************/

// Enlace del footer que abre/cierra el formulario
const botonAbrir = document.getElementById("enlace-formulario");

// Sección completa del formulario (oculta por defecto)
const seccionFormulario = document.getElementById("formulario");

// El propio formulario HTML
const form = document.getElementById("form-suscripcion");

// Párrafo donde se mostrarán los mensajes de error/éxito
const mensaje = document.getElementById("mensaje-formulario");

/****************************************************
 * 2) MOSTRAR / OCULTAR FORMULARIO AL HACER CLIC
 ****************************************************/

// Cuando el usuario haga clic en "Suscríbete al diario"...
botonAbrir.addEventListener("click", function (e) {

  // Evita que el enlace haga su acción normal (navegar a # o recargar)
  e.preventDefault();

  // Alterna la clase "visible": si estaba oculto lo muestra, si estaba visible lo oculta
  seccionFormulario.classList.toggle("visible");
});

/****************************************************
 * 3) VALIDACIÓN DEL FORMULARIO AL ENVIARLO
 ****************************************************/

// Cuando el usuario pulse el botón "Suscribirme"...
form.addEventListener("submit", function (e) {

  // Evita el envío real del formulario (lo pide la práctica)
  e.preventDefault();

  // Limpiar mensajes previos antes de validar
  mensaje.innerHTML = "";
  mensaje.style.color = "";

  // Obtener valores escritos por el usuario
  const nombre = document.getElementById("nombre").value.trim();
  const email = document.getElementById("email").value.trim();
  const suscripcion = document.getElementById("suscripcion").value;
  const seccion = document.getElementById("seccion").value;
  const condiciones = document.getElementById("condiciones").checked;

  // Array donde guardaremos los mensajes de error
  let errores = [];


  /***************************************
   * 4 COMPROBACIONES
   ***************************************/

  // Nombre obligatorio
  if (nombre === "") errores.push("El nombre no puede estar vacío.");

  // Email obligatorio + debe contener @
  if (email === "") errores.push("El email no puede estar vacío.");
  else if (!email.includes("@")) errores.push("El email debe incluir @.");

  // Tipo de suscripción obligatorio
  if (suscripcion === "") errores.push("Debes seleccionar un tipo de suscripción.");

  // Sección favorita obligatoria
  if (seccion === "") errores.push("Debes seleccionar una sección favorita.");

  // Aceptar condiciones obligatorio
  if (!condiciones) errores.push("Debes aceptar las condiciones.");



  /***************************************
   * 5) MOSTRAR ERRORES O ÉXITO
   ***************************************/

  if (errores.length > 0) {

    // Si hay errores, los mostramos en ROJO 
    // Usamos <br> para que haya un salto de linea entre mensajes de error.
    mensaje.innerHTML = errores.join("<br>");
    mensaje.style.color = "red";

  } else {

    // Si todo está correcto, mensaje en VERDE
    mensaje.innerHTML = "Formulario enviado correctamente.";
    mensaje.style.color = "green";

    // Vacía todos los campos del formulario
    form.reset();
  }
});"}

